import React, { PureComponent } from "react";
import Konva from "konva";
import { Circle, Image } from "react-konva";
import { WIDTH, HEIGHT } from "./Field";
import useImage from 'use-image';

const MIN_X = 12,
    MIN_Y = 12,
    MAX_X = WIDTH - MIN_X,
    MAX_Y = HEIGHT - MIN_Y,
    SPEED = 30;

//useImage('https://png.pngtree.com/svg/20170706/cruise_ship_428210.png');

export default class Ball extends PureComponent {
    state = {
        color: this.props.color,
        x: MIN_X,
        y: MIN_Y,
        direction: { x: 0, y: 0 },
        image: null
    };

    componentDidMount() {
        this.animate();
    }
    loadImage(){
        this.image = new Window.Image();
        this.image.src = this.props.src;
        this.image.addEventListener('load', this.handleLoad);
    }

    newCoord = (val, delta, max, min) => {
        let newVal = val + delta;
        let newDelta = delta;

        if(newVal > max){
            newVal = min + newVal - max
        }

        if(newVal < min ){
            newVal = max - newVal
        }

        return { val: newVal, delta: newDelta };
    };

    animate = () => {
        const { x, y } = this.state,
            { horizontal, vertical } = this.props;

        if (horizontal !== 0 || vertical !== 0) {
            const newX = this.newCoord(x, horizontal, MAX_X, MIN_X);
            const newY = this.newCoord(y, vertical, MAX_Y, MIN_Y);

            //console.log(newX, newY);

            this.setState({
                x: newX.val,
                y: newY.val,
                direction: {
                    x: newX.delta,
                    y: newY.delta
                }
            });
        }

        this.animationTimeout = setTimeout(this.animate, 50);
    };

    render() {
        const { color, x, y } = this.state;

        return (
            <Image
                x={x}
                y={y}
                image={this.state.image}
                ref={node => {
                    this.imageNode = node;
                }}
            />
        );
    }

    componentWillUnmount() {
        clearTimeout(this.animationTimeout);
    }
}

