import React, { Component } from "react";
import { Layer } from "react-konva";
import Field from "./Field";
import Ball from "./Ball";
import KeyHandler from "react-key-handler";
import io from "socket.io-client";

export default class Game extends Component {

    constructor(props){
        super(props);

        this.state = {
            vertical: 0,
            horizontal: 0,
            players: []
        };

        this.socket = io('localhost:4000');

        this.socket.emit('new player');
        setInterval(()=> {
            this.socket.emit('movement', this.state)
        }, 1000/60);

        this.socket.on('state', (players) => {
            this.setState({players});
        })
    }

    keys = ["w", "a", "s", "d"];

    handleKeyDown = event => {
        switch (event.key) {
            case "w":
                this.setState({ vertical: -10 });
                break;
            case "s":
                this.setState({ vertical: 10 });
                break;
            case "a":
                this.setState({ horizontal: -10 });
                break;
            case "d":
                this.setState({ horizontal: 10 });
                break;
            default:
                console.log("down: " + event.key);
        }
    };
    handleKeyUp = event => {
        switch (event.key) {
            case "w":
                this.setState({ vertical: 0 });
                break;
            case "s":
                this.setState({ vertical: 0 });
                break;
            case "a":
                this.setState({ horizontal: 0 });
                break;
            case "d":
                this.setState({ horizontal: 0 });
                break;
            default:
                console.log("up: " + event.key);
        }
    };
    render() {
        return (
            <Layer>
                <Field />
                {this.state.players.map(player => {
                    <Ball
                        vertical={player.x}
                        horizontal={player.y}
                    />
                })}
                {this.keys.map(key => (
                    <KeyHandler
                        keyEventName="keydown"
                        key={key}
                        keyValue={key}
                        onKeyHandle={this.handleKeyDown}
                    />
                ))}
                {this.keys.map(key => (
                    <KeyHandler
                        keyEventName="keyup"
                        key={key}
                        keyValue={key}
                        onKeyHandle={this.handleKeyUp}
                    />
                ))}
            </Layer>
        );
    }
}
