import React, { PureComponent } from "react";
import Konva from "konva";
import { Circle, Shape } from "react-konva";
import { WIDTH, HEIGHT } from "./Field";
import useImage from 'use-image';

const MIN_X = 12,
    MIN_Y = 12,
    MAX_X = WIDTH - MIN_X,
    MAX_Y = HEIGHT - MIN_Y,
    SPEED = 30;

export default class Ball extends PureComponent {

    state = {
        color: this.props.color,
        x: MIN_X,
        y: MIN_Y,
        direction: { x: 0, y: 0 }
    };

    componentDidMount() {
        this.animate();
    }

    newCoord = (val, delta, max, min) => {
        let newVal = val + delta;
        let newDelta = delta;

        if(newVal > max){
            newVal = min + newVal - max
        }

        if(newVal < min ){
            newVal = max - newVal
        }

        return { val: newVal, delta: newDelta };
    };

    animate = () => {
        const { x, y } = this.state,
            { horizontal, vertical } = this.props;

        if (horizontal !== 0 || vertical !== 0) {
            const newX = this.newCoord(x, horizontal, MAX_X, MIN_X);
            const newY = this.newCoord(y, vertical, MAX_Y, MIN_Y);

            //console.log(newX, newY);

            this.setState({
                x: newX.val,
                y: newY.val,
                direction: {
                    x: newX.delta,
                    y: newY.delta
                }
            });
        }
        this.animationTimeout = setTimeout(this.animate, 50);
    };

    render() {
        const { color, x, y } = this.state;

        return (
            <Shape
                ref={comp => {
                    this.ball = comp;
                }}
                x={x}
                y={y}
                ceneFunc={(context, shape) => {
                    context.beginPath();
                    context.moveTo(20, 50);
                    context.lineTo(220, 80);
                    context.quadraticCurveTo(150, 100, 260, 170);
                    context.closePath();
                    // (!) Konva specific method, it is very important
                    context.fillStrokeShape(shape);
                }}
                fill="#00D2FF"
                stroke="black"
                strokeWidth={4}
            />

        );
    }

    componentWillUnmount() {
        clearTimeout(this.animationTimeout);
    }
}

