import React, { PureComponent } from "react";
import Konva from "konva";
import { Circle } from "react-konva";
import { WIDTH, HEIGHT } from "./Field";

const MIN_X = 12,
    MIN_Y = 12,
    MAX_X = WIDTH - MIN_X,
    MAX_Y = HEIGHT - MIN_Y,
    SPEED = 30;

const imageSrc = '/Users/eminebahce/Desktop/Development/Game/client/game/src/ship.png';
export default class Ball extends PureComponent {

    constructor(...args) {
        super(...args);
        const img = new window.Image();
        img.src = img;
        img.onload = () => {
            this.setState({
                fillPatternImage: img
            })
        };

        this.state = {
            color: this.props.color,
            x: MIN_X,
            y: MIN_Y,
            direction: { x: 0, y: 0 }
        };
    }



    componentDidMount() {
        this.animate();
    }

    newCoord = (val, delta, max, min) => {
        let newVal = val + delta;
        let newDelta = delta;

        if(newVal > max){
            newVal = min + newVal - max
        }

        if(newVal < min ){
            newVal = max - newVal
        }

        return { val: newVal, delta: newDelta };
    };

    animate = () => {
        const { x, y } = this.state,
            { horizontal, vertical } = this.props;

        if (horizontal !== 0 || vertical !== 0) {
            const newX = this.newCoord(x, horizontal, MAX_X, MIN_X);
            const newY = this.newCoord(y, vertical, MAX_Y, MIN_Y);

            //console.log(newX, newY);

            this.setState({
                x: newX.val,
                y: newY.val,
                direction: {
                    x: newX.delta,
                    y: newY.delta
                }
            });
        }

        this.animationTimeout = setTimeout(this.animate, 50);
    };

    render() {
        const { color, x, y } = this.state;

        return (
            <Circle
                ref={comp => {
                    this.ball = comp;
                }}
                x={x}
                y={y}
                radius={10}
                fill={color}
                shadowBlur={1}
                fillPatternImage={this.state.fillPatternImage}
            />
        );
    }

    componentWillUnmount() {
        clearTimeout(this.animationTimeout);
    }
}
