import { WIDTH, HEIGHT } from "../components/Field";

const MIN_X = 12,
  MIN_Y = 12,
  MAX_X = WIDTH - MIN_X,
  MAX_Y = HEIGHT - MIN_Y;

newCoord = (val, delta, max, min) => {
  let newVal = val + delta;
  let newDelta = delta;

  if (newVal > max) {
    newVal = min + newVal - max;
  }

  if (newVal < min) {
    newVal = max - newVal;
  }

  return { val: newVal, delta: newDelta };
};

const animate = (x, y, horizontal, vertical) => {
  if (horizontal !== 0 || vertical !== 0) {
    const newX = this.newCoord(x, horizontal, MAX_X, MIN_X);
    const newY = this.newCoord(y, vertical, MAX_Y, MIN_Y);

    return {
      x: newX.val,
      y: newY.val,
      direction: {
        x: newX.delta,
        y: newY.delta
      }
    };
  }

  // this.animationTimeout = setTimeout(this.animate, 5);
};

export default animate;
